import React, { useState, useEffect } from "react";
import items from './products.json';
import Checkout from "./Checkout.js";
const Shop = () => {
    const [cart, setCart] = useState([]); // State variable to store the cart items
    const [cartTotal, setCartTotal] = useState(0); // State variable to store the total price of the cart items
    const [searchTerm, setSearchTerm] = useState(""); // State variable to store the search term


    const Checkout = ({ cart }) => {
        return (
            <div className="container">
                <h2>Checkout</h2>
                {cart && cart.length > 0 ? (
                    <table className="table">
                        <thead>
                            <tr>
                                <th>Product</th>
                                <th>Price</th>
                            </tr>
                        </thead>
                        <tbody>
                            {cart.map((item) => (
                                <tr key={item.id}>
                                    <td>{item.title}</td>
                                    <td>${item.price}</td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                ) : (
                    <p>No items in the cart</p>
                )}
            </div>
        );
    };

    // Function to calculate how many of a particular product are in the cart
    function howManyofThis(id) {
        let hmot = cart.filter((cartItem) => cartItem.id === id);
        return hmot.length;
    }

    // Function to calculate the total price of the items in the cart
    const total = () => {
        let totalVal = 0;
        for (let i = 0; i < cart.length; i++) {
            totalVal += cart[i].price;
        }
        // Round the total to 2 decimal places
        totalVal = totalVal.toFixed(2);
        setCartTotal(totalVal);
    };

    // Update total whenever cart changes
    useEffect(() => {
        total();
    }, [cart]);

    // Function to add a product to the cart
    const addToCart = (el) => {
        setCart([...cart, el]);
    };

    // Function to remove a product from the cart
    const removeFromCart = (el) => {
        let itemFound = false;
        //const updatedCart = cart.filter((item) => item.id !== el.id || item.quantity > 1);
        const updatedCart = cart.filter((item) => {
            if (item.id === el.id && !itemFound) {
                itemFound = true;
                return false;
            }
            return true;
        });
        if (itemFound) {
            setCart(updatedCart);
        }
    };

    // Filter products based on search term
    const filteredItems = items.filter((item) =>
        item.title.toLowerCase().includes(searchTerm.toLowerCase())
    );

    // Mapping over the products to display them
    const listItems = filteredItems.reduce((rows, product, index) => {
        if (index % 3 === 0) {
            rows.push([]);
        }
        rows[rows.length - 1].push(product);
        return rows;
    }, []).map((rowProducts, rowIndex) => (
        <div className="row" key={rowIndex} >
            {rowProducts.map((product) => (
                <div className="col-md-4" key={product.id} >
                    <div className="">
                        <img className="card img-fluid" src={product.image} alt={product.title} />
                        <h5 className="card-title mt-2 mb-0 font-weight-bold">{product.title}</h5>
                        <p className="card-text text-muted">${product.price}
                            <span className="close">&#10005;</span>{howManyofThis(product.id)}
                            <button type="button" style={{ marginLeft: '5%' }} className="btn btn-light" onClick={() => removeFromCart(product)}> - </button>
                            <button type="button" className="btn btn-light" onClick={() => addToCart(product)}> + </button>
                        </p>
                    </div>
                </div>
            ))}
        </div>
    ));

    return (
        <div className="container-fluid d-flex justify-content-start">
            <div className="">
                <div className="row">
                    <div className="col-md-12 cart" style={{ marginLeft: '25%' }}>
                        <div className="row">
                            <div className="col-md-6">
                                <input
                                    type="text"
                                    className="form-control mt-3 mb-3"
                                    style={{ width: '350px' }}
                                    placeholder="Search..."
                                    value={searchTerm}
                                    onChange={(e) => setSearchTerm(e.target.value)}
                                />
                            </div>
                        </div>
                        <div className="title">
                            <div className="row">
                                <div className="col">
                                    <h4><b>Products</b></h4>
                                </div>
                                <div className="col align-self-center text-right text-muted">
                                    Products selected {cart.length}
                                </div>
                                <div className="col">
                                    <span className="small text-muted" id="order">Order total:</span>
                                    <span className="lead fw-normal" id="order2">${cartTotal}</span>
                                </div>
                            </div>
                        </div>
                        <div>{listItems}</div>
                        <button className="btn btn-light bg-warning container-fluid d-flex justify-content-center mt-2" onClick={Checkout}>
                            Checkout
                        </button>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default Shop;
