import React, { useState, useEffect } from "react";
import items from './products.json';

const Shop = () => {
    const [cart, setCart] = useState([]);
    const [cartTotal, setCartTotal] = useState(0);

    function howManyofThis(id) {
        let hmot = cart.filter((cartItem) => cartItem.id === id);
        return hmot.length;
    }

    const total = () => {
        let totalVal = 0;
        for (let i = 0; i < cart.length; i++) {
            totalVal += cart[i].price;
        }
        setCartTotal(totalVal);
    };

    useEffect(() => {
        total();
    }, [cart]);

    const addToCart = (el) => {
        setCart([...cart, el]);
    };

    const removeFromCart = (el) => {
        let hardCopy = [...cart];
        hardCopy = hardCopy.filter((cartItem) => cartItem.id !== el.id);
        setCart(hardCopy);
    };

    const listItems = items.map((el, index) => {
        // Calculate the index of the first product in the current row
        const startIndex = index * 3;

        // Get the products for the current row
        const rowProducts = items.slice(startIndex, startIndex + 3);

        // Map over the products in the current row
        return (
            <div className="row border-top border-bottom" key={index}>
                {rowProducts.map((product) => (
                    <div className="col" key={product.id}>
                        {/* PRODUCT */}
                        <div className="row main align-items-center">
                            <div className="col-2">
                                <img className="img-fluid" src={product.image} alt={product.title} />
                            </div>
                            <div className="col">
                                <div className="row text-muted">{product.title}</div>
                                <div className="row">{product.category}</div>
                            </div>
                            <div className="col">
                                <button type="button" variant="light" onClick={() => removeFromCart(product)}> - </button>{" "}
                                <button type="button" variant="light" onClick={() => addToCart(product)}> + </button>
                            </div>
                            <div className="col">
                                ${product.price} <span className="close">&#10005;</span>{howManyofThis(product.id)}
                            </div>
                        </div>
                    </div>
                ))}
            </div>
        );
    });

}
export default Shop;
