import React, { useState, useEffect } from "react";
import items from './products.json';

const Shop = () => {
    const [selectedItems, setSelectedItems] = useState([]); // Array to store selected items
    const [searchTerm, setSearchTerm] = useState(""); // State variable to store the search term

    // Function to add a product to the selected items array
    const addToSelectedItems = (el) => {
        setSelectedItems([...selectedItems, el]);
    };

    // Function to remove a product from the selected items array
    const removeFromSelectedItems = (el) => {
        const updatedItems = selectedItems.filter((item) => item.id !== el.id);
        setSelectedItems(updatedItems);
    };

    // Filter products based on search term
    const filteredItems = items.filter((item) =>
        item.title.toLowerCase().includes(searchTerm.toLowerCase())
    );

    // Mapping over the products to display them
    const listItems = filteredItems.map((product) => (
        <div className="col-md-4" key={product.id} >
            <div className="">
                <img className="card img-fluid" src={product.image} alt={product.title} />
                <h5 className="card-title mt-2 mb-0 font-weight-bold">{product.title}</h5>
                <p className="card-text text-muted">${product.price}
                    <span className="close">&#10005;</span>{selectedItems.filter((item) => item.id === product.id).length}
                    <button type="button" style={{ marginLeft: '5%' }} className="btn btn-light" onClick={() => removeFromSelectedItems(product)}> - </button>
                    <button type="button" className="btn btn-light" onClick={() => addToSelectedItems(product)}> + </button>
                </p>
            </div>
        </div>
    ));

    return (
        <div className="container-fluid d-flex justify-content-start">
            <div className="">
                <div className="row">
                    <div className="col-md-12 cart" style={{ marginLeft: '25%' }}>
                        <div className="row">
                            <div className="col-md-6">
                                <input
                                    type="text"
                                    className="form-control mt-3 mb-3"
                                    style={{ width: '350px' }}
                                    placeholder="Search..."
                                    value={searchTerm}
                                    onChange={(e) => setSearchTerm(e.target.value)}
                                />
                            </div>
                        </div>
                        <div>{listItems}</div>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default Shop;
