import React from "react";

const Checkout = ({ cart }) => {
    return (
        <div className="container">
            <h2>Checkout</h2>
            {cart && cart.length > 0 ? (
                <table className="table">
                    <thead>
                        <tr>
                            <th>Product</th>
                            <th>Price</th>
                        </tr>
                    </thead>
                    <tbody>
                        {cart.map((item) => (
                            <tr key={item.id}>
                                <td>{item.title}</td>
                                <td>${item.price}</td>
                                <td>{item.image}</td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            ) : (
                <p>No items in the cart</p>
            )}
        </div>
    );
};

export default Checkout;
