import React, { useState, useEffect } from "react";
import items from './products.json';
import Checkout from './Checkout'; // Import the Checkout component

const Shop = () => {
    const [cart, setCart] = useState([]); // State variable to store the cart items
    const [cartTotal, setCartTotal] = useState(0); // State variable to store the total price of the cart items
    const [searchTerm, setSearchTerm] = useState(""); // State variable to store the search term
    const [checkoutClicked, setCheckoutClicked] = useState(false); // State variable to track if the checkout button is clicked

    // Function to calculate how many of a particular product are in the cart
    function howManyofThis(id) {
        let hmot = cart.filter((cartItem) => cartItem.id === id);
        return hmot.length;
    }

    // Function to calculate the total price of the items in the cart
    const total = () => {
        let totalVal = 0;
        for (let i = 0; i < cart.length; i++) {
            totalVal += cart[i].price;
        }
        // Round the total to 2 decimal places
        totalVal = totalVal.toFixed(2);
        setCartTotal(totalVal);
    };

    // Update total whenever cart changes
    useEffect(() => {
        total();
    }, [cart]);

    // Function to add a product to the cart
    const addToCart = (el) => {
        setCart([...cart, el]);
    };

    // Function to remove a product from the cart
    const removeFromCart = (el) => {
        let itemFound = false;
        const updatedCart = cart.filter((item) => {
            if (item.id === el.id && !itemFound) {
                itemFound = true;
                return false;
            }
            return true;
        });
        if (itemFound) {
            setCart(updatedCart);
        }
    };

    // Filter products based on search term
    const filteredItems = items.filter((item) =>
        item.title.toLowerCase().includes(searchTerm.toLowerCase())
    );

    // Function to handle checkout button click
    const handleCheckoutClick = () => {
        setCheckoutClicked(true);
    };

    return (
        <div className="container-fluid d-flex justify-content-start">
            <div className="">
                <div className="row">
                    <div className="col-md-12 cart" style={{ marginLeft: '25%' }}>
                        <div className="row">
                            <div className="col-md-6">
                                <input
                                    type="text"
                                    className="form-control mt-3 mb-3"
                                    style={{ width: '350px' }}
                                    placeholder="Search..."
                                    value={searchTerm}
                                    onChange={(e) => setSearchTerm(e.target.value)}
                                />
                            </div>
                        </div>
                        <div className="title">
                            <div className="row">
                                <div className="col">
                                    <h4><b>Products</b></h4>
                                </div>
                                <div className="col align-self-center text-right text-muted">
                                    Products selected {cart.length}
                                </div>
                                <div className="col">
                                    <span className="small text-muted" id="order">Order total:</span>
                                    <span className="lead fw-normal" id="order2">${cartTotal}</span>
                                </div>
                            </div>
                        </div>
                        {/* Render Checkout component if checkout button is clicked */}
                        {checkoutClicked ? <Checkout cart={cart} /> : (
                            <>
                                <div>{listItems}</div>
                                <button className="btn btn-light bg-warning container-fluid d-flex justify-content-center mt-2" onClick={handleCheckoutClick}>
                                    Checkout
                                </button>
                            </>
                        )}
                    </div>
                </div>
            </div>
        </div>
    );
};

export default Shop;
