import React, { useState, useEffect } from "react";
import Shop from './Shopping_bootstrap.js';

const ThreeViews = () => {
    const [oneView, setOneView] = useState(false); // browser view
    const [secondView, setsecondView] = useState(false); //cart view
    const [thirdView, sethirdView] = useState(false); // confirmation view

    const setView1 = () => {
        if (oneView === false) { // value and type "==="
            setOneView(true);
        }
        else {
            setOneView(false);
        }
    }

    const setView2 = () => {
        if (secondView === false) { 
            setsecondView(true);
        }
        else {
            setsecondView(false);
        }
    }

    const setView3 = () => {
        if (thirdView === false) { 
            sethirdView(true);
        }
        else {
            sethirdView(false);
        }
    }

    function View1() {
        return (
            <Shop />
        );
    }

    function View2() {
        return (
            <div>
                <h1>This is View2</h1>
                <img src="https://fakestoreapi.com/img/71-3HjGNDUL._AC_SY879._SX._UX._SY._UY_.jpg" width={200} alt="View2"></img>
            </div>
        );
    }

    function View3() {
        return (
            <div>
                <h1>This is View3</h1>
                <img src="https://fakestoreapi.com/img/71li-ujtlUL._AC_UX679_.jpg" width={200} alt="View3"></img>
            </div>
        );
    }

    return (
        <div>
            {/* <button onClick={setView1}>One</button>
            <button onClick={setView2}>Two</button>
            <button onClick={setView3}>Three</button> */}
            <Shop />
            <button className="btn bg-warning container-fluid d-flex justify-content-center mt-2" onClick={setView2}>Checkout</button>
            {secondView && <View2 />}
            {thirdView && <View3 />}

        </div>
    );
}

export default ThreeViews;
