import React, { useState, useEffect } from "react";
import Shop from './Shopping_bootstrap.js';

const ThreeViews = () => {
    const [oneView, setOneView] = useState(false); // browser view
    const [secondView, setsecondView] = useState(false); //cart view
    const [thirdView, sethirdView] = useState(false); // confirmation view

    const setView1 = () => {
        setOneView(true);
        setsecondView(false);
        sethirdView(false);
    }

    const setView2 = () => {
        setOneView(false);
        setsecondView(true);
        sethirdView(false);
    }

    const setView3 = () => {
        setOneView(false);
        setsecondView(false);
        sethirdView(true);
    }

    function View1() {
        return (
            <Shop />
        );
    }

    function View2() {
        return (
            <div>
                <h1>This is View2</h1>
                <img src="https://fakestoreapi.com/img/71-3HjGNDUL._AC_SY879._SX._UX._SY._UY_.jpg" width={200} alt="View2"></img>
            </div>
        );
    }

    function View3() {
        return (
            <div>
                <h1>This is View3</h1>
                <img src="https://fakestoreapi.com/img/71li-ujtlUL._AC_UX679_.jpg" width={200} alt="View3"></img>
            </div>
        );
    }

    return (
        <div>
            <button className="btn btn-light bg-warning" onClick={setView1}>View 1</button>
            <button className="btn btn-light bg-warning" onClick={setView2}>View 2</button>
            <button className="btn btn-light bg-warning" onClick={setView3}>View 3</button>
            {oneView && <View1 />}
            {secondView && <View2 />}
            {thirdView && <View3 />}
        </div>
    );
}

export default ThreeViews;
