import React, { useState, useEffect } from "react";
import items from './products.json';

const Shop = () => {
    const [cart, setCart] = useState([]); // State variable to store the cart items
    const [cartTotal, setCartTotal] = useState(0); // State variable to store the total price of the cart items

    // Function to calculate how many of a particular product are in the cart
    function howManyofThis(id) {
        let hmot = cart.filter((cartItem) => cartItem.id === id);
        return hmot.length;
    }

    // Function to calculate the total price of the items in the cart
    const total = () => {
        let totalVal = 0;
        for (let i = 0; i < cart.length; i++) {
            totalVal += cart[i].price;
        }
        setCartTotal(totalVal);
    };

    // Update total whenever cart changes
    useEffect(() => {
        total();
    }, [cart]);

    // Function to add a product to the cart
    const addToCart = (el) => {
        setCart([...cart, el]);
    };

    // Function to remove a product from the cart
    const removeFromCart = (el) => {
        const updatedCart = cart.filter((item) => item.id !== el.id || item.quantity > 1);
        setCart(updatedCart);
    };
    // Function to decrease the quantity of a product in the cart by 1
    const decreaseQuantity = (product) => {
        const updatedCart = [...cart];
        const index = updatedCart.findIndex((item) => item.id === product.id);
        if (index !== -1 && updatedCart[index].quantity > 0) {
            updatedCart[index].quantity -= 1;
        }
        setCart(updatedCart);
    };

    // Mapping over the products to display them
    const listItems = items.map((el, index) => {
        const startIndex = index * 3;
        const rowProducts = items.slice(startIndex, startIndex + 3);

        return (
            <div className="row border-top border-bottom" key={index}>
                {rowProducts.map((product) => (
                    <div className="col" key={product.id}>
                        <div className="row main align-items-center">
                            <div className="col-2">
                                <img className="img-fluid" src={product.image} alt={product.title} style={{ width: '150px', height: '150px' }} />
                                {/* Increase the width and height of the product image */}
                            </div>
                            <div className="col">
                                <div className="row text-muted">{product.title}</div>
                                <div className="row">{product.category}</div>
                            </div>
                            <div className="col">
                                <button type="button" variant="light" onClick={() => decreaseQuantity(product)}> - </button>{" "}
                                <button type="button" variant="light" onClick={() => addToCart(product)}> + </button>
                            </div>
                            <div className="col">
                                ${product.price} <span className="close">&#10005;</span>{howManyofThis(product.id)}
                            </div>
                        </div>
                    </div>
                ))}
            </div>
        );
    });

    return (
        <div>
            STORE SE/ComS319
            <div className="card">
                <div className="row">
                    <div className="col-md-8 cart">
                        <div className="title">
                            <div className="row">
                                <div className="col">
                                    <h4>
                                        <b>Products</b>
                                    </h4>
                                </div>
                                <div className="col align-self-center text-right text-muted">
                                    Products selected {cart.length}
                                </div>
                                <div className="col">
                                    <span className="small text-muted" id="order">Order total:</span>
                                    <span className="lead fw-normal" id="order2">${cartTotal}</span>
                                </div>
                            </div>
                        </div>
                        <div>{listItems}</div>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default Shop;
