import React, { useState, useEffect } from "react";

const Checkout = ({ cart, cartTotal, onReturnClick }) => {
    const [name, setName] = useState(""); // State variable to store the name
    const [email, setEmail] = useState(""); // Store the email
    return (
        <div className="container">
            <div className="row mb-3">
                <div className="col">
                    <h2>Checkout</h2>
                </div>
                <div className="col">
                    <button className="btn bg-warning mt-1" onClick={onReturnClick}>Back</button>
                </div>
            </div>
            {cart && cart.length > 0 ? (
                <table className="table">
                    <thead>
                        <tr>
                            <th style={{ width: '33%' }}>Product</th>
                            <th style={{ width: '33%' }}>Price</th>
                            <th style={{ width: '33%' }}>Image</th>
                        </tr>
                    </thead>
                    <tbody>
                        {cart.map((item) => (
                            <tr key={item.id}>
                                <td>{item.title}</td>
                                <td>${item.price}</td>
                                <td><img src={item.image} alt={item.title} style={{ width: '100%', height: 'auto' }} /></td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            ) : (
                <p>No items in the cart</p>
            )}

            <div className="row">
                <div className="col">
                    <span className="small text-muted" id="order">Order total(with tax):</span>
                    <span className="lead fw-normal" id="order2">${(cartTotal * 1.1).toFixed(2)}</span>
                </div>
            </div>


            <div className="col">
                <h2>Payment Information</h2>
                <div className="row" >
                    <input
                        type="text"
                        className="form-control mt-3 mb-3"
                        style={{ width: '350px' }}
                        placeholder="Enter Full Name"
                        value={name}
                        onChange={(e) => setName(e.target.value)}
                    />
                    <input
                        type="text"
                        className="form-control mt-3 mb-3"
                        style={{ width: '350px' }}
                        placeholder="Email"
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                    />
                </div>

                <input
                    type="text"
                    className="form-control mt-3 mb-3"
                    style={{ width: '350px' }}
                    placeholder="Email"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                />

                <input
                    type="number"
                    className="form-control mt-3 mb-3"
                    style={{ width: '350px' }}
                    placeholder="XXXX-XXXX-XXXX-XXXX(Card)"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                />


            </div>
        </div>
    );
};

export default Checkout;
