import React from "react";

const Checkout = ({ cart, cartTotal, onReturnClick }) => {
    return (
        <div className="container">
            <div className="row mb-3"> 
                <div className="col">
                    <h2>Checkout</h2>
                </div>
                <div className="col"> 
                    <button className="btn bg-warning" onClick={onReturnClick}>Back</button>
                </div>
            </div>
            {cart && cart.length > 0 ? (
                <table className="table">
                    <thead>
                        <tr>
                            <th style={{ width: '33%' }}>Product</th> 
                            <th style={{ width: '33%' }}>Price</th>
                            <th style={{ width: '33%' }}>Image</th> 
                        </tr>
                    </thead>
                    <tbody>
                        {cart.map((item) => (
                            <tr key={item.id}>
                                <td>{item.title}</td>
                                <td>${item.price}</td>
                                <td><img src={item.image} alt={item.title} style={{ width: '100%', height: 'auto' }} /></td> {/* Set image width to 100% */}
                            </tr>
                        ))}
                    </tbody>
                </table>
            ) : (
                <p>No items in the cart</p>
            )}

            <div className="row"> {/* Add margin top to create space between the content and the order total */}
                <div className="col">
                    <span className="small text-muted" id="order">Order total(with tax):</span>
                    <span className="lead fw-normal" id="order2">${(cartTotal * 1.1).toFixed(2)}</span>
                </div>
            </div>
        </div>
    );
};

export default Checkout;
