import React from "react";

const Checkout = ({ cart,cartTotal }) => {
    return (
        <div className="container">
            <h2>Checkout</h2>
            <div className="col">
            <span className="small text-muted" id="order">Order total(with tax):</span>
            <span className="lead fw-normal" id="order2">${(cartTotal*1.1).toFixed(2)}</span>
            </div>
            {cart && cart.length > 0 ? (
                <table className="table col">
                    <thead>
                        <tr>
                            <th>Product</th>
                            <th>Price</th>
                            <th>Image</th> {/* New column for image */}
                        </tr>
                    </thead>
                    <tbody>
                        {cart.map((item) => (
                            <tr key={item.id}>
                                <td>{item.title}</td>
                                <td>${item.price}</td>
                                <td><img src={item.image} alt={item.title} style={{ width: '250px', height: '250px' }} /></td> 
                            </tr>
                        ))}
                    </tbody>
                </table>
            ) : (
                <p>No items in the cart</p>
            )}
        </div>
    );
};

export default Checkout;
